import classes from "./Inputs.module.css";
import Button from "../ui/button/Button";
import { useState } from "react";

export default function Inputs(props) {
  const initStates = {
    currState: 100,
    ySavState: 1200,
    expState: 24000,
    durState: 2,
  };

  // const [currState, setCurrState] = useState(100);
  // const [ySavState, setYSavState] = useState(1200);
  // const [expState, setExpState] = useState(24000);
  // const [durState, setDurState] = useState(2);

  // Another solution
  const [InputsState, setInputs] = useState(initStates);

  const changeHandler = (e) => {
    setInputs({ ...InputsState, [e.target.id]: e.target.value });
  };
  const submitHandler = (e) => {
    e.preventDefault();
    props.onCalculate(InputsState);
  };

  // const submitHandler = (e) => {
  //   e.preventDefault();
  //   const InputsState = {
  //     // return an object
  //     currSave: +currState,
  //     yearSave: +ySavState,
  //     expSave: +expState,
  //     durSave: +durState,
  //     [e.target.id]: e.target.value,
  //   };
  //   console.log(InputsState);
  //   props.onCalculate(InputsState);
  // };

  const resetHandler = (e) => {
    e.preventDefault();
    // setCurrState(0);
    // setYSavState(0);
    // setExpState(0);
    // setDurState(0);
  };

  // const changeHandler = (id, value) => {
  //   setInputs((preInputs) => {
  //     return {
  //       ...preInputs,
  //       [id]: +value,
  //     };
  //   });
  // };

  return (
    <form
      className={classes.form}
      onSubmit={submitHandler}
      onReset={resetHandler}
    >
      <div className={classes["input-group"]}>
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            id="current-savings"
            key="current-savings"
            value={currState}
            onChange={changeHandler}
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            key="yearly-contribution"
            value={ySavState}
            onChange={changeHandler}
          />
        </p>
      </div>
      <div className={classes["input-group"]}>
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id="expected-return"
            key="expected-return"
            value={expState}
            onChange={changeHandler}
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            key="duration"
            value={durState}
            onChange={changeHandler}
          />
        </p>
      </div>

      <p className={classes.actions}>
        <Button
          type="reset"
          // className={classes.buttonAlt} // WORKS ALSO
          // onClick={resetHandler} //  WORKS ALSO
        >
          Reset
        </Button>
        <Button type="submit">Calculate</Button>
      </p>
    </form>
  );
}
