import { useState } from "react";
import Header from "./components/header/Header";
import Inputs from "./components/inputs/Inputs";
import Outputs from "./components/outputs/Outputs";
const yearlyData = [];

function App() {
  const calculateHandler = (userInput) => {
    // const [state, setState] = useState("");
    if (userInput) {
      let currentSavings = +userInput["currSave"];
      const yearlyContribution = +userInput.yearSave;
      const expectedReturn = +userInput.expSave / 100;
      const duration = +userInput["durSave"];

      for (let i = 0; i < duration; i++) {
        const yearlyInterest = currentSavings * expectedReturn;
        currentSavings += yearlyInterest + yearlyContribution;
        yearlyData.push({
          year: i + 1,
          yearlyInterest: yearlyInterest,
          savingsEndOfYear: currentSavings,
          yearlyContribution: yearlyContribution,
        });
      }
    }
  };

  return (
    <>
      <Header />
      <Inputs onCalculate={calculateHandler} />
      {/* Todo: Show below table conditionally (only once result data is available) */}
      {/* Show fallback text if no data is available */}
      <Outputs data={yearlyData} intInv={userInput.currSave} />
    </>
  );
}

export default App;
