const resetHandler = (e) => {
  e.preventDefault();
  setInputs(initStates);
};
import classes from "./Inputs.module.css";
import Button from "../ui/button/Button";
import { useState } from "react";

export default function Inputs(props) {
  const initStates = {
    currState: 100,
    ySavState: 1200,
    expState: 24000,
    durState: 2,
  };

  let [InputsState, setInputs] = useState(initStates);

  // const changeHandler = (id, value) => {
  //   setInputs((preInputs) => {
  //     return {
  //       ...preInputs,
  //       [id]: +value,
  //     };
  //   });
  // };

  const changeHandler = (e) => {
    setInputs({ ...InputsState, [e.target.id]: e.target.value });
  };
  const submitHandler = (e) => {
    e.preventDefault();
    props.onCalculate(InputsState);
  };

  const resetHandler = (e) => {
    e.preventDefault();
    InputsState = initStates;
  };

  return (
    <form
      className={classes.form}
      onSubmit={submitHandler}
      onReset={resetHandler}
    >
      <div className={classes["input-group"]}>
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            id="current-savings"
            key="current-savings"
            value={InputsState.currState}
            onChange={changeHandler}
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            key="yearly-contribution"
            value={InputsState.ySavState}
            onChange={changeHandler}
          />
        </p>
      </div>
      <div className={classes["input-group"]}>
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id="expected-return"
            key="expected-return"
            value={InputsState.expState}
            onChange={changeHandler}
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            key="duration"
            value={InputsState["durState"]}
            onChange={changeHandler}
          />
        </p>
      </div>

      <p className={classes.actions}>
        <Button
          type="reset"
          // className={classes.buttonAlt} // WORKS ALSO
          // onClick={resetHandler} //  WORKS ALSO
        >
          Reset
        </Button>
        <Button type="submit">Calculate</Button>
      </p>
    </form>
  );
}
